(function($){
    $.fn.textFitWithResolution = function(){

        //Twitter and facebook Feed
        var fbTextContainerWidth = Math.floor((($('#facebookFeed a').width()-5)/8)*2);
        var twTextContainerWidth = Math.floor((($('#twitterFeed a').width()-5)/8)*2);

        var fbText = $("#facebookFeed a").text();
        var twText = $("#twitterFeed a").text();
        if(fbTextContainerWidth>0 && fbText.length > fbTextContainerWidth){
            $("#facebookFeed a").text(fbText.substr(0, (fbTextContainerWidth-4))+" ...");
        }
        if(twTextContainerWidth>0 && twText.length > twTextContainerWidth){
            $("#twitterFeed a").text(twText.substr(0, (twTextContainerWidth-4))+" ...");
        }

    }
})(jQuery);

/**
 * Custom plugin to make text animation
 *
 * @autho: Julfiker
 */
(function($) {
    $.fn.textAnimate = function(options) {

        //default settings
        var defaultSettings = {
            move: "left",
            speed: 4000,
            reset: false
        };

        var settings = $.extend({}, defaultSettings, options);

        if(settings.move=="left")
            $(this).animate({
                opacity:1
            //marginLeft: "3%"
            }, settings.speed);
        else if(settings.move=="right")
            $(this).animate({
                opacity:1,
                marginRight: "20%"
            }, settings.speed);

    }
})(jQuery);
